INSERT INTO tb_job (name, description_job, salary, type_Of_Candidate) VALUES ('Desenvolvedor Junior', 'Buscamos uma Desenvolvedora de Software Júnior apaixonada por programação para integrar nossa equipe dinâmica. Como parte do time, você terá a oportunidade de contribuir para projetos desafiadores, expandir suas habilidades técnicas e crescer profissionalmente. Se você é entusiasta, proativa e deseja iniciar sua carreira no mundo da programação, esta vaga é para você!', 3000, 'MULHERES');
INSERT INTO tb_job (name, description_job, salary, type_Of_Candidate) VALUES ('Gerente de RH', 'Estamos em busca de um profissional apaixonado e comprometido para ocupar a posição de Gerente de Recursos Humanos (RH) em nossa empresa. Valorizamos a diversidade e estamos empenhados em promover um ambiente de trabalho inclusivo e equitativo. Encorajamos especialmente candidaturas de pessoas negras, que trazem perspectivas únicas e experiências valiosas.', 6000, 'PESSOAS NEGRAS');
INSERT INTO tb_job (name, description_job, salary, type_Of_Candidate) VALUES ('Assistente de PCP', 'Estamos buscando um profissional dedicado para integrar nossa equipe como Assistente de PCP em nossa empresa. Valorizamos a diversidade e estamos comprometidos em oferecer oportunidades igualitárias a todas as pessoas, incluindo aquelas com deficiência. Encorajamos especialmente candidaturas de profissionais com deficiência, pois acreditamos que a diversidade fortalece nossa equipe e impulsiona a inovação.', 2000, 'PCD');
